import { Button } from '@mui/material';
import React from 'react'
import ReactPlayer from 'react-player';
import { useState } from 'react';
function Feed() {
    const [playing,setPlaying] = useState(true);
    const [videoNo,setVideoNo] = useState(0);
    const handlePlaying = () => {
        setPlaying(!playing);
    }
    const videos=["http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ElephantsDream.mp4","https://player.vimeo.com/external/372335193.sd.mp4?s=80151fa22b2eba81883c8641f2c9e493762c7357&profile_id=164&oauth2_token_id=57447761"]
    const handleVideoIncrease = () => {
        console.log(videoNo);
        if(videoNo < videos.length - 1) {
            setVideoNo(videoNo + 1);
        }
    }

    const handleVideoDecrease = () => {
        if(videoNo > 0) {
            setVideoNo(videoNo - 1);
        }
    }
    return (
        <div className='flex flex-col w-full h-screen justify-center items-center mt-10'>
            <div>
                <Button variant='outlined'>Upload Video</Button>
            </div>
            <div className='flex justify-center items-center h-full'>
                <div  onClick = {handlePlaying} className='height-[100%] width-[40%]'>
                    <ReactPlayer playing= {playing} width='40%'
                        loop={true} height='100%' url={videos[videoNo]} controls={false}></ReactPlayer>
                </div>
                <div>
                    <div onClick = {handleVideoDecrease} className="top-0 p-2 shadow-lg cursor-pointer shadow-grey-800 rounded-lg material-symbols-outlined">
                        arrow_upward
                    </div>
                    <div onClick = {handleVideoIncrease} className="bottom-0 p-2 shadow-lg cursor-pointer shadow-grey-800 rounded-lg material-symbols-outlined">
                        arrow_downward
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Feed
