import React from 'react'
import Videos from './Videos';
import './Feed.css'
import { useContext } from 'react';
import Authcontext from './AuthContextProvider';
import { Navigate } from 'react-router';
import {Input} from '@mui/material';
import {Button} from '@mui/material'
import axios from 'axios';
import api from '../utility'
function Feed() {
    const userData = useContext(Authcontext);
    const handleUploadVideo = async (e) => {
        e.preventDefault();
        const file = document.querySelector("#video").files[0];
        const data = new FormData();
        data.append("video",file);
        console.log(file.filename);
        const res = await axios.post("http://localhost:8080/videouplaod",data,{
            headers: {
                "Content-Type": "multipart/form-data",
            }
        });
        if(res.status === 201) {
            const resToAddToDatabase = await api.post('post/createPost',{
                video_url : file.filename,
                user_id : userData.user.id
            });
            if(resToAddToDatabase.status === 201) {
                window.alert("Video Uploaded SuccessFully");
            } else {
                window.alert("Unable to Add Video");
            }
        } else {
            window.alert("Unable To Add Video");
        }
    }

    const videos = []
    return (
        userData.user ? <div className='flex flex-col w-full h-full justify-center items-center mt-10'>
            <div>
                <form onSubmit = {handleUploadVideo} encType="multipart/form-data">
                    <Input type = 'file' id = "video" variant='outlined'></Input>
                    <Button type="submit">upload</Button>
                </form>
            </div>
            <div className='flex flex-col justify-between items-center videocontainer'>
                {videos.map(video => {
                    return <Videos videoLink={video}></Videos>
                })}
            </div>
        </div> : <Navigate to='/login'></Navigate>
    )
}

export default Feed
